mapper.select:
select ctg.*, group_concat(distinct CONCAT(cpd.id, ':', cpd.platform_name)) as platforms, group_concat(distinct CONCAT(ccd.id, ':', ccd.category_name)) as categories from cartridge ctg left join cartridge_platform_relation cpr on ctg.id = cpr.cartridge_id left join cartridge_platform_dict cpd on cpr.platform_id = cpd.id
left join cartridge_category_relation ccr on ctg.id = ccr.cartridge_id left join cartridge_category_dict ccd on ccr.category_id = ccd.id
left join user_cartridge_relation ucr on ctg.id = ucr.cartridge_id and #{userId} = ucr.user_id where 1=1
and ctg.title like #{pattern}
and exists (select 1 from cartridge_platform_relation cpr2 left join cartridge_platform_dict cpd2 on cpr2.platform_id = cpd2.id and ctg.id = cpr2.cartridge_id where cpd2.platform_name in
#{platformName} )
and exists(select 1 from cartridge_category_relation ccr2 left join cartridge_category_dict ccd2 on ccr2.cartridge_id = ccd2.id and ctg.id= ccr2.cartridge_id where ccd2.category_name in
#{categoryName} )
and ( ctg.${sortField} (select ${sortField} from cartridge where id = #{cursorId}) or (ctg.${sortField} = (select ${sortField} from cartridge where id = #{cursorId}) and ctg.id #{cursorId}) )
and ctg.id #{cursorId}
group by ctg.id order by
ctg.${sortField} ${sortDirection}, ctg.id ${sortDirection}
ctg.id ${sortDirection}
limit #{pageSize}
